  const [homeState, setHomeState] = useState(true);


  return (
    <body>
      <Home/>
      {!homeState && <Header homeState={homeState} setHomeState={setHomeState}/>}

      <main>
      {!homeState && <About/>}
        -- CONTACT FORM - display if selected
      </main>

      <Footer/>
    </body>
  );
}

export default App;
