import React, { useState } from "react";

function Nav(props) {
    const {setHomeState} = props;
    const [aboutSelected, setAboutSelected] = useState(false);

    return (
        <>
        <ul>
            <li>
                <a href="#project" onClick={() => setHomeState(false)}>PROJECTS</a>
            </li>
            <li>
                <a href="#about" 
                    onClick={() => {
                        setHomeState(false)
                        setAboutSelected(true)
                    }} className={aboutSelected && 'navActive'} >ABOUT
                </a>
            </li>
            <li>
                <a href="Resume/LisaLeResume.pdf" target="_blank">RESUME</a>
            </li>
        </ul>

        <div id="navigation" class="logo">
            <a className="slice" href="/" onClick={()=> setHomeState(true)}>
                <div className="top"><span>Lisa Le</span></div>
                <div className="bottom"><span>Lisa Le</span></div>
            </a>
        </div>
        </>
    )
}

export default Nav;
